pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import { ERC20 } from "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";


/**
 * @title Humanity
 * @dev ERC20 token that can be used to vote on applications to the Humanity registry.
 */
contract Humanity is ERC20 {

    string public constant name = "Rapport";
    string public constant symbol = "RAP";
    uint8 public constant decimals = 18;
    string public version = "0.0.1";

    uint public constant INITIAL_SUPPLY = 250000000e18; // 250 million
    uint public constant FINAL_SUPPLY = 10000000000e18; // 10 billion

    address public registry;

    constructor(address _registry) public {
        registry = _registry;
        _mint(msg.sender, INITIAL_SUPPLY);
    }

    function mint(address account, uint256 value) public {
        require(msg.sender == registry, "Rapport::mint: Only the registry can mint new tokens");
        require(totalSupply().add(value) <= FINAL_SUPPLY, "Rapport::mint: Exceeds final supply");

        _mint(account, value);
    }

}
