pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import { IERC20 } from "./interface/IERC20.sol";
import { ERC20Taxable } from "./ERC20Taxable.sol";
import { SafeMath } from "openzeppelin-solidity/contracts/math/SafeMath.sol";

/**
 * @title Society0xDonation
 * @dev This smart contract serves the purpose of bootstrapping the full-time development of society0x
 */
contract Society0xDonation {

    using SafeMath for uint256;

    struct Donor {
        address id;
        uint256[] donations;
        string pseudonym;
    }

    struct Pledge {
        uint256 id;
        address pledgerAddress;
        uint256 pledgeToMilestone;
        uint256 daiValue;
        uint256 blockNumber;
        uint256 blockTimestamp;
    }

    struct FundMetaData {
        uint256 daiReceived;
        uint256 createdBlockTimestamp;
        string coverPictureIpfsHash;
        address beneficiary;
    }

    event PledgeEvent (
        uint256 daiPledgeValue,
        uint256 daiTotal,
        uint256 blockNumber,
        uint256 blockTimestamp
    );

    IERC20 public dai;
    address public beneficiary;
    Pledge[] public pledges;
    uint256[] public milestones;
    uint256[] public milestonesWithdrawn;
    uint256[] public milestonesReached;
    uint256 public daiReceived;
    string public coverPictureIpfsHash;
    uint256 public createdBlockTimestamp;

    constructor(IERC20 _dai, address _beneficiary, string memory _coverPictureIpfsHash) public  {
        dai = _dai;
        beneficiary = _beneficiary;
        milestones.push(0);
        milestones.push(40000000000000000000000);
        daiReceived = 0;
        coverPictureIpfsHash = _coverPictureIpfsHash;
        createdBlockTimestamp = block.timestamp;
    }

    function getFundMetaData() public view returns(uint256, uint256, string memory, address) {
        return (
            daiReceived,
            createdBlockTimestamp,
            coverPictureIpfsHash,
            beneficiary
        );
    }

    function donate(uint256 _daiValue, string memory _pseudonym) public returns (bool) {
        require(
            (_daiValue > 0),
            "Society0xDonation::donate: DAI value must be more than zero."
        );
        require(
            dai.allowance(msg.sender, address(this)) >= _daiValue,
            "Society0xDonation::donate: DAI allowance is not sufficient for making this donation."
        );
        require(
            dai.transferFrom(msg.sender, address(this), _daiValue),
            "Society0xDonation::donate: DAI transfer failed, make sure DAI balance is sufficient."
        );
        uint256 newBalance = daiReceived.add(_daiValue);
        daiReceived = newBalance;
        if((milestones[milestonesReached.length] > 0) && (newBalance >= milestones[milestonesReached.length])){
            milestonesReached.push(milestonesReached.length);
        }
        Pledge memory pledge = Pledge(
            pledges.length,
            msg.sender,
            milestones[milestones.length - 1],
            _daiValue,
            block.number,
            block.timestamp
        );
        pledges.push(pledge);
        emit PledgeEvent(_daiValue, newBalance, block.number, block.timestamp);
        return true;
    }

    function forwardToBeneficiary() public returns(bool) {
        require(
            milestones.length > 0,
            "Society0xDonation::forwardToBeneficiary: No milestone set."
        );
        require(
            daiReceived >= milestones[milestones.length.sub(1)],
            "Society0xDonation::forwardToBeneficiary: daiReceived has not met milestone."
        );
        uint256 forwardingValue = 0;
        if(milestones.length > 1){
            //Current milestone value minus previous milestone value
            forwardingValue = milestones[milestones.length.sub(1)].sub(milestones[milestones.length.sub(2)]);
        }else if(milestones.length == 1){
            //Current milestone value
            forwardingValue = milestones[milestones.length.sub(1)];
        }
        require(
            forwardingValue > 0,
            "Society0xDonation::forwardToBeneficiary: Failure from forwardingValue of 0."
        );
        require(
            dai.transfer(beneficiary, forwardingValue),
            "Society0xDonation::forwardToBeneficiary: forwardingValue transfer failed."
        );
        return true;
    }

    function getPledgeCount() public view returns(uint256) {
        return pledges.length;
    }

    function getMilestoneCount() public view returns(uint256) {
        return milestones.length;
    }

    function getPledgeByPledgeId(uint256 _pledgeIndex) public view returns(
        uint256,
        address,
        uint256,
        uint256,
        uint256,
        uint256
    ) {
        require(
            (pledges.length > 0),
            "Society0xDonation::getPledgeByPledgeId: No Pledges Exist."
        );
        require(
            (pledges[_pledgeIndex].pledgerAddress != 0x0000000000000000000000000000000000000000),
            "Society0xDonation::getPledgeByPledgeId: Pledge ID Does Not Exist."
        );
        Pledge memory pledge = pledges[_pledgeIndex];
        return(
            pledge.id,
            pledge.pledgerAddress,
            pledge.pledgeToMilestone,
            pledge.daiValue,
            pledge.blockNumber,
            pledge.blockTimestamp
        );
    }

}
